CREATE TABLE MAPS
(
	CODE VARCHAR2(5) NOT NULL,
	NAME VARCHAR2(50) NOT NULL,
	CONTENT CLOB
);
CREATE UNIQUE INDEX PK_MAPS ON MAPS(CODE);

CREATE TABLE MAPS_TABLES
(
    TABLE_NAME VARCHAR2(30) NOT NULL,
    SEQUENCES VARCHAR2(255),
	MAXBY VARCHAR2(255),
	ORDERBY VARCHAR2(255),
	PK_COLUMNS VARCHAR2(255),
	OFF_COLUMNS VARCHAR2(255),
	BK_TABLE_NAME VARCHAR2(30),
	ENABLED CHAR DEFAULT 1 CHECK (ENABLED IN (0,1))
);
CREATE UNIQUE INDEX PK_MAPS_TABLES ON MAPS_TABLES(TABLE_NAME);
CREATE UNIQUE INDEX PK_MAPS_BK_TABLES ON MAPS_TABLES(BK_TABLE_NAME);

CREATE TABLE MAPS_TKT_TABLES
(
    TABLE_NAME VARCHAR2(30) NOT NULL,
    DESCRIPTION VARCHAR2(255),
	SELECTCLAUSE VARCHAR2(255),
	ENABLED CHAR DEFAULT 1 CHECK (ENABLED IN (0,1))
);
CREATE UNIQUE INDEX PK_MAPS_TKT_TABLES ON MAPS_TKT_TABLES(TABLE_NAME);


CREATE TABLE MAPS_IDS(
    TABLE_NAME VARCHAR2(30) NOT NULL,
    OLD_ID NUMBER(10),
    NEW_ID NUMBER(10)
);


CREATE TABLE MAPS_FK
(
	TABLE_NAME VARCHAR2(30) NOT NULL,
	COLUMN_NAME VARCHAR2(30) NOT NULL,
	FK_NAME VARCHAR2(30),
	R_TABLE_NAME VARCHAR2(30) NOT NULL,
	R_COLUMN_NAME VARCHAR2(30) NOT NULL
);


