---GENERATE_INSERT.BEGIN(EMAFI_REPORT WHERE 1 = 1), p_recursive = true, p_simpleInsert = true
INSERT INTO EMAFI_REPORT(REPORT_TYPE, ETAT_TYPE, NAME, PARAMETERVALUES, REPORT_ORDER, ENABLED, WORKSHEET, QUERYDESC, QUERY) VALUES('A', 'C', 'Bilan Actif', '{Titre}=Bilan Actif', 1, 1, 'Bilans', 'l,c255,c4,c255,l,d,d', '
SELECT ID_RUBRIC, RUBRIC, RUBRIC_TYPE, LABEL, NIV,
    SUM(CASE WHEN AMOUNT < 0 AND ACCOUNT_NUMBER IN ({BANKS}) THEN 0 ELSE AMOUNT END) AMOUNT,
    SUM(CASE WHEN AMOUNT_PREC < 0 AND ACCOUNT_NUMBER IN ({BANKS}) THEN 0 ELSE AMOUNT_PREC END) AMOUNT_PREC,
    RUBRIC_ORDER, LABEL_ORDER 
FROM 
    (SELECT ID_RUBRIC, RUBRIC, RUBRIC_TYPE, LABEL, NIV, SUM(AMOUNT) AMOUNT, SUM(AMOUNT_PREC) AMOUNT_PREC, RUBRIC_ORDER, LABEL_ORDER, ACCOUNT_NUMBER 
    FROM 
        (SELECT EP.ID_RUBRIC, EP.RUBRIC, EP.RUBRIC_TYPE, EP.LABEL, EP.NIV, EP.REPORT_TYPE, 
            DECODE(EC.SENS, ''D'', 1, ''C'', -1, 0) * EC.AMOUNT AMOUNT, 
            DECODE(EC.SENS, ''D'', 1, ''C'', -1, 0) * CASE WHEN TRUNC(EC.{DATE_TYPE}) <= TO_DATE(''{START_DATE}'', ''DD/MM/YYYY'') THEN EC.AMOUNT ELSE 0 END AMOUNT_PREC, 
            EP.RUBRIC_ORDER, EP.LABEL_ORDER, EP.ACCOUNT_NUMBER, EP.ACCOUNT_TYPE, EC.STATUS 
        FROM V_EMAFI_PARAMETERS EP 
            LEFT JOIN EMAFI_CRE EC ON EC.ACCOUNT_NUMBER = EP.ACCOUNT_NUMBER 
                AND TRUNC(EC.{DATE_TYPE}) <= TO_DATE(''{END_DATE}'', ''DD/MM/YYYY'')
                AND EC.PTF_ID IN (SELECT IDENT FROM FOLIO START WITH IDENT = {FOLIO_ID} CONNECT BY PRIOR IDENT = MGR) 
                AND EC.STATUS IN ({STATUS_LIST}) 
        WHERE EP.REPORT_TYPE = ''{REPORT_TYPE}'')
    GROUP BY ID_RUBRIC, RUBRIC, RUBRIC_TYPE, LABEL, NIV, RUBRIC_ORDER, LABEL_ORDER, ACCOUNT_NUMBER) 
GROUP BY ID_RUBRIC, RUBRIC, RUBRIC_TYPE, LABEL, NIV, RUBRIC_ORDER, LABEL_ORDER 
ORDER BY RUBRIC_ORDER, LABEL_ORDER');
