INSERT INTO EMAFI_REPORT(REPORT_TYPE, ETAT_TYPE, NAME, PARAMETERVALUES, REPORT_ORDER, ENABLED, WORKSHEET, QUERYDESC, QUERY) VALUES('BL', 'C', 'Balance', '', 9, 1, 'Balance', 'l,c255,c4,c255,l,c3,c50,d,c2,d,d,d,d,d,c2', '
SELECT ID_LABEL, LABEL, RUBRIC_TYPE, ACCOUNT_NUMBER, NIV, CURRENCY, ACCOUNT_DESC, 
