INSERT INTO EMAFI_REPORT(REPORT_TYPE, ETAT_TYPE, NAME, PARAMETERVALUES, REPORT_ORDER, ENABLED, WORKSHEET, QUERYDESC, QUERY) VALUES('ESG', 'C', 'Etat des soldes de gestion', '{Titre}=Etat des soldes de gestion;{TableHeader1}=Tableau d''analyse de l''actif net;{TableFooter1}=NB:;{TableHeader2}=Tableau d''analyse de la valeur liquidative', 12, 1, 'Bilans', 'l,c255,c4,c255,l,d,d,c255,c255@l,c255,c4,c255,l,d,d,c255,c255', '
WITH CPC AS (
SELECT RO,
    SUM(CASE WHEN A < 0 AND AN IN ({BANKS}) THEN 0 ELSE A END) A,
    SUM(CASE WHEN A1 < 0 AND AN IN ({BANKS}) THEN 0 ELSE A1 END) A1
FROM
    (SELECT SUM(A) A, SUM(A1) A1, RO, AN 
    FROM
        (SELECT EP.RUBRIC_ORDER RO, EP.ACCOUNT_NUMBER AN,
			DECODE(EC.SENS, ''D'', 1, ''C'', -1, 0) * EC.AMOUNT A, 
			DECODE(EC.SENS, ''D'', 1, ''C'', -1, 0) * CASE WHEN TRUNC(EC.{DATE_TYPE}) <= TO_DATE(''{START_DATE}'', ''DD/MM/YYYY'') THEN EC.AMOUNT ELSE 0 END A1
        FROM V_EMAFI_PARAMETERS EP 
            LEFT JOIN EMAFI_CRE EC ON EC.ACCOUNT_NUMBER = EP.ACCOUNT_NUMBER 
                AND TRUNC(EC.{DATE_TYPE}) <= TO_DATE(''{END_DATE}'', ''DD/MM/YYYY'')
                AND EC.PTF_ID IN (SELECT IDENT FROM FOLIO START WITH IDENT = {FOLIO_ID} CONNECT BY PRIOR IDENT = MGR)
                AND EC.STATUS IN ({STATUS_LIST})
        WHERE EP.REPORT_TYPE = ''CPC'' AND RUBRIC_TYPE = ''R'')
    GROUP BY RO, AN)
GROUP BY RO),
CPC1 AS (SELECT SUM(A) A, SUM(A1) A1, 1 RO FROM CPC WHERE RO <= 2),
CPC2 AS (SELECT SUM(A) A, SUM(A1) A1, 2 RO FROM CPC WHERE RO <= 5),
CPC3 AS (SELECT SUM(A) A, SUM(A1) A1, 3 RO FROM CPC WHERE RO <= 8)
SELECT E.IDR, E.R || ''^^^=IF(RC[-2]>RC[-1], RC[-2]-RC[-1], 0)^=IF(RC[-2]>RC[-3], RC[-2]-RC[-3], 0)'' R, E.RT, E.L, E.NIV,
    CASE WHEN C.RO IS NULL THEN E.A ELSE C.A END A,
    CASE WHEN C.RO IS NULL THEN E.A1 ELSE C.A1 END A1,
    ''=IF(RC[-2]>RC[-1], RC[-2]-RC[-1], 0)'' X, ''=IF(RC[-2]>RC[-3], RC[-2]-RC[-3], 0)'' Y, E.RO
FROM
(SELECT T.IDR, T.R, T.RT, T.L, T.NIV, T.A, T.A1, RO, LO FROM (
SELECT IDR, R, RT, L, NIV, RO, LO,
	SUM(CASE WHEN A < 0 AND AN IN ({BANKS}) THEN 0 ELSE A END) A,
	SUM(CASE WHEN A1 < 0 AND AN IN ({BANKS}) THEN 0 ELSE A1 END) A1
FROM
	(SELECT IDR, R, RT, L, NIV, SUM(A) A, SUM(A1) A1, RO, LO, AN
	FROM
		(SELECT EP.ID_RUBRIC IDR, EP.RUBRIC R, EP.RUBRIC_TYPE RT, EP.LABEL L, EP.NIV, EP.REPORT_TYPE,
			DECODE(EC.SENS, ''D'', 1, ''C'', -1, 0) * EC.AMOUNT A,
			DECODE(EC.SENS, ''D'', 1, ''C'', -1, 0) * CASE WHEN TRUNC(EC.{DATE_TYPE}) <= TO_DATE(''{START_DATE}'', ''DD/MM/YYYY'') THEN EC.AMOUNT ELSE 0 END A1,
            EP.RUBRIC_ORDER RO, EP.LABEL_ORDER LO, EP.ACCOUNT_NUMBER AN, EP.ACCOUNT_TYPE, EC.STATUS
        FROM V_EMAFI_PARAMETERS EP
            LEFT JOIN EMAFI_CRE EC ON EC.ACCOUNT_NUMBER LIKE EP.ACCOUNT_NUMBER
                AND TRUNC(EC.{DATE_TYPE}) <= TO_DATE(''{END_DATE}'', ''DD/MM/YYYY'')
                AND EC.PTF_ID IN (SELECT IDENT FROM FOLIO START WITH IDENT = {FOLIO_ID} CONNECT BY PRIOR IDENT = MGR)
                AND EC.STATUS IN ({STATUS_LIST})
        WHERE EP.REPORT_TYPE = ''{REPORT_TYPE}'')
	GROUP BY IDR, R, RT, L, NIV, RO, LO, AN)
GROUP BY IDR, R, RT, L, NIV, RO, LO) T) E
LEFT JOIN (SELECT T.A, T.A1, T.RO FROM (SELECT * FROM CPC1 UNION SELECT * FROM CPC2 UNION SELECT * FROM CPC3) T) C
ON E.RO = C.RO ORDER BY E.RO, E.LO@
SELECT R.IDR, NULL R, R.RT, R.R L, R.NIV, R.A, R.A1, ''=IF(RC[-2]>RC[-1], RC[-2]-RC[-1], 0)'' X, ''=IF(RC[-2]>RC[-3], RC[-2]-RC[-3], 0)'' Y FROM (
SELECT IDR, ''L'' RT, R, NIV, SUM(A) A, SUM(A1) A1, RO
FROM ({T1})
WHERE (R LIKE ''%(A)'' OR R LIKE ''%(B)'' OR R LIKE ''%(C)'') AND NVL(NIV, 1) = 1
GROUP BY IDR, ''L'', R, NIV, RO UNION
SELECT 1, ''L'', ''Valeur liquididative de clôture'', 1,
    (SELECT NVL(SUM(FHG.SHARE_PRICE), 0) FROM FUND_HISTORY_GLOBAL FHG WHERE FHG.CODE = F.SICOVAM AND FHG.NAV_DATE = TO_DATE(''{END_DATE}'', ''DD/MM/YYYY'')) A,
	(SELECT NVL(SUM(FHG.SHARE_PRICE), 0) FROM FUND_HISTORY_GLOBAL FHG WHERE FHG.CODE = F.SICOVAM AND FHG.NAV_DATE = TO_DATE(''{START_DATE}'', ''DD/MM/YYYY'')) A1, 1
FROM TITRES F WHERE F.MNEMO IN (SELECT IDENT FROM FOLIO START WITH IDENT = {FOLIO_ID} CONNECT BY PRIOR IDENT = MGR) UNION
SELECT 2, ''L'', ''Valeur liquidative unitaire de clôture = (B) / (C)'', 1, 0, 0, 6 RO FROM DUAL UNION
SELECT 3, ''L'', ''Dont part en capital = (B) - (A) / (C)'', 1, 0, 0, 7 RO FROM DUAL UNION
SELECT 4, ''L'', ''Dont part en revenu = (A) / (C)'', 1, 0, 0, 8 RO FROM DUAL) R ORDER BY RO');
